class CartesiNodesAnalytics():
    def __init__(self, address, validator_address=None):
        self.w3 = Web3(Web3.HTTPProvider('https://mainnet.infura.io/v3/aaa403aaa21a42758699d70f75d827f5'))
        self.address = self.w3.to_checksum_address(address)
        self.token_contract = self.w3.eth.contract(address=ctsi_token_contract, abi=ctsi_token_abi)
        self.validator_address = validator_address
        if validator_address:
            pool_abi = cartesi_colossus_pool_abi
            pool = self.w3.to_checksum_address(validator_address)
            self.pool_contract = self.w3.eth.contract(address=pool, abi=pool_abi)

    def get_total_balance(self):
        self.available_balance = self.token_contract.functions.balanceOf(self.address).call() / 10 ** 18
        self.total_balance = self.available_balance
        if self.validator_address:
            self.get_delegated_balance()
            self.total_balance += self.delegated_balance + self.locked_balance

    def get_delegated_balance(self):
        '''
        Get the amount of CTSI delegated to a validator
        '''
        obj = self.pool_contract.functions.userBalance(self.address).call()
        shares = obj[1]
        self.delegated_balance = self.pool_contract.functions.sharesToAmount(shares).call() / 10 ** 18
        self.locked_balance = obj[0] / 10 ** 18


def get_cartesi_delegator_balance(address, validator_address):
    price = get_binance_current_priceCOSM('CTSI')
    cc = CartesiNodesAnalytics(address, validator_address)
    cc.get_total_balance()
    res = {'available': cc.available_balance, 'delegated': cc.delegated_balance,  'unbonding': cc.locked_balance,
           'rewards_to_claim': 0,'total_balance': cc.total_balance, 'price': price}
    return res

def get_binance_current_priceCOSM(coin):
    r=requests.get(f"https://api.binance.com/api/v3/ticker/price?symbol={coin}USDT").json()

    return float(r['price'])
